package com.desafio.tecnico.configurationimport io.swagger.v3.oas.models.examples.Exampleimport io.swagger.v3.oas.models.info.Infoimport io.swagger.v3.oas.models.media.Contentimport io.swagger.v3.oas.models.media.MediaTypeimport org.springdoc.core.customizers.OpenApiCustomizerimport org.springframework.context.annotation.Beanimport org.springframework.context.annotation.Configuration@Configurationclass SwaggerConfig {    @Bean    fun openApiCustomizer(): OpenApiCustomizer {        return OpenApiCustomizer { openApi ->            openApi.info(                Info()                    .title("API de Solicitação de Cartões")                    .version("1.0.0")                    .description("API para verificação de elegibilidade e solicitação de cartões")            )            val pathItem = openApi.paths.getOrDefault("/cartoes", null)            pathItem?.post?.requestBody?.content = Content().apply {                addMediaType("application/json", MediaType().apply {                    examples = mapOf(                        "exemplo1" to Example().apply {                            summary = "Exemplo de Cliente"                            value = mapOf(                                "cliente" to mapOf(                                    "nome" to "Cliente Teste",                                    "cpf" to "123.456.789-10",                                    "idade" to 25,                                    "data_nascimento" to "2000-01-01",                                    "uf" to "SP",                                    "renda_mensal" to 4000,                                    "email" to "cliente@teste.com",                                    "telefone_whatsapp" to "11999992020"                                )                            )                        }                    )                })            }        }    }}